package javaproject;

public class Assignmentoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1= 21;
		num1+=5; // action is num1 = num1+5
		System.out.println("num1+= 5"+num1);
		num1-=5; // action is num1 = num1-5
		System.out.println("num1-= 5"+num1);
		num1*=5; // action is num1 = num1 x 5
		System.out.println("num1*= 5"+num1);
		num1/=5; // action is num1 = num1/5
		System.out.println("num1/= 5"+num1);
		num1%=5; // action is num1 = num1%5
		System.out.println("num1%= 5"+num1);
	}

}
